INSERT INTO artist (id, name, statement, notes, display)
	VALUES(316, 'anne knight',
	'<p class="artist">I am a self taught mixed media artist from Liverpool, producing an array of colourful collage artwork that incorporates paper, paint, thread, buttons and handmade clay embellishments.</p>

